function escapeString(str) {
  return str.replace(/"/g, "'");
}


let id = 0;
let id_post = 1;
posts.forEach((obj, index) => {
    const dom = new JSDOM(obj.html);
    const document = dom.window.document;

    // Chọn tất cả các thẻ <figure>
    const figures = document.querySelectorAll('figure');

    // Lặp qua từng thẻ <figure>
    figures.forEach(figure => {
        const file = files[id++];

        // Tạo thẻ <p> mới
        const newParagraph = document.createElement('p');
        const newImage = document.createElement('img');
        // Đặt thuộc tính cho thẻ <img> mới
        newImage.src = `https://strapi.vnst.io/uploads/${file.hash}.png`;
        newImage.alt = file.name;

        newParagraph.appendChild(newImage);

        // Thay thế thẻ <figure> bằng thẻ <p>
        figure.parentNode.replaceChild(newParagraph, figure);
    });

    // Lấy lại chuỗi HTML sau khi thay đổi
    const updatedHTMLString = escapeString(document.body.innerHTML);
    const updatePosts = `UPDATE posts SET content = "${updatedHTMLString}" where id = ${id_post++};\n`;
    fs.appendFile('updatePost.sql', updatePosts, (err) => {
        if (err) console.error('Error writing to file', err);
        else console.log('File has been written');
    });     

    // console.log(updatedHTMLString + '\n\n\n');
    // console.log(id);
});